mapper.select:
select a.nickname,a.headimgurl,a.openid,a.totalDonateAmount,a.donateCount FROM ( SELECT u.nickname,u.headimgurl,u.openid, sum(t.donateAmount) as totalDonateAmount, c.donateCount FROM cu.cu_donate_list t LEFT JOIN portal.userinfo u on t.openId = u.openid left join (select b.openId, count(1) as donateCount from cu_donate_list b where b.openId = b.openId GROUP BY b.openId)c on c.openId = t.openId WHERE u.appid = #{appid,jdbcType=VARCHAR}
and t.projectId = #{projectId,jdbcType=VARCHAR}
GROUP BY u.nickname,u.headimgurl,u.openid ) a
ORDER BY ${orderBy}
ORDER BY a.totalDonateAmount DESC, a.donateCount desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}
