mapper.sql:
from fop_association
where status != 0

    and id = #{condition.id,jdbcType=VARCHAR}


    and status = #{condition.status,jdbcType=CHAR}


    and fullName like
    concat('%',#{condition.fullName,jdbcType=VARCHAR},'%')


    and phoneNumber = #{condition.phoneNumber,jdbcType=CHAR}

mapper.select:
select




        ORDER BY ${orderBy}


        ORDER BY createDate


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
from fop_association
where phoneNumber = #{phoneNumber,jdbcType=VARCHAR}
or fullName = #{fullName,jdbcType=VARCHAR}
