mapper.sql:
from information_service t
where t.status  <>  0

    and t.title like
    concat('%',#{condition.title,jdbcType=VARCHAR},'%')


    and t.modules =#{condition.modules,jdbcType=CHAR}


    and t.status = #{condition.status,jdbcType=CHAR}
