mapper.sql:
from portal.department t
left join portal.province on t.area_code =province.code
LEFT JOIN portal.department d on t.parent_department_id = t.department_id
where t.syid = #{condition.syid}

    and t.department_name like
    concat('%',#{condition.departmentName,jdbcType=VARCHAR},'%')


    and
    (
    t.parent_department_id = #{condition.parentDepartmentId,jdbcType=VARCHAR}
    or t.department_id=#{condition.parentDepartmentId,jdbcType=VARCHAR}
    )


    and t.status = #{condition.status,jdbcType=VARCHAR}


    and t.type = #{condition.type,jdbcType=VARCHAR}


    and t.area_code like concat(#{condition.areaCode,jdbcType=VARCHAR},'%')

mapper.select:
select IFNULL((select 1 from fundtown.vip_publicity vp where vp.deptId=t.department_id),'0') as publicStatus, d.department_name as parent_department_name, t.department_id, t.parent_department_id, t.department_name, t.short_name, t.create_time, t.create_user_id, t.status, t.category, t.area_code, province.name area_name, t.pcode, t.contact_name, t.contact_tel, t.contact_mobile, t.contact_qq, t.contact_email, t.contact_fax, t.legal_person_name, t.legal_person_id_type, t.legal_person_id_no, t.legal_person_tel, t.legal_person_mobile, t.legal_person_addr, t.reg_date, t.reg_capital, t.reg_area_code, t.reg_addr, t.nature, t.buss_license_no, t.buss_addr, t.buss_area_code, t.fax, t.email, t.trans_depart_approval_no, t.trans_buss_license_no, t.trans_buss_license_valid_date, t.trans_buss_scope, t.employees_num, t.driver_num, t.unlicensed_driver_num, t.licensed_driver_num, t.type, t.simage, t.bimage, t.create_user_id, t.create_user_name, t.last_modify_time, t.last_modify_user_id, t.last_modify_user_name, t.latitude, t.longitude, t.serviceTimeStart, t.serviceTimeEnd, t.qrcode, t.serviceWay
ORDER BY ${orderBy}
ORDER BY t.create_time desc
limit #{start,jdbcType=DECIMAL},#{limit,jdbcType=DECIMAL}

select count(1)
from portal.department
where  status = '1'
and department_name = #{departmentName,jdbcType=VARCHAR}
and syid = #{syid}

select count(1)
from portal.department
where contact_email = #{email,jdbcType=VARCHAR}

select count(1)
from portal.department
where buss_license_no = #{bussLicenseNo,jdbcType=VARCHAR}
