mapper.select:
select t.id, t.aiCode, t.lnkCode, t.lnkType, t.prePlayUrl, t.remark, t.status, t.createDate, b.name as topBuildingName, b.address as address, b.subareaCode as subareaCode, s.name as subareaName, r.id as a_id, r.code, r.`name`, r.prePlayUrl, r.preImgUrl , r.duration, r.type, r.playUrl, r.rsoWidth, r.rsoHeight from anima_lnk t LEFT JOIN top_building b on t.lnkCode = b.code LEFT JOIN top_subarea s on b.subareaCode = s.code LEFT JOIN anima_res r on t.aiCode = r.code where 1=1
and t.lnkCode =#{condition.lnkCode,jdbcType=VARCHAR}
ORDER BY ${orderBy}
ORDER BY t.createDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
from anima_lnk t
where t.aiCode = #{aiCode}
and t.lnkCode = #{lnkCode}
