mapper.select:
select t.id, t.code, t.`name`, t.type, t.duration, t.playUrl, t.prePlayUrl, t.preImgUrl, t.rsoWidth, t.rsoHeight, t.remark, t.status, t.createUserId, t.createUserName, t.createDate, t.lastModifyUserId, t.lastModifyUserName, t.lastModifyDate from anima_res t
ORDER BY ${orderBy}
ORDER BY t.createDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
where 1 = 1

  and t.code =#{condition.code,jdbcType=VARCHAR}


  and t.status =#{condition.status,jdbcType=CHAR}


  and t.type =#{condition.type,jdbcType=VARCHAR}


  and (t.`name` like concat('%',#{condition.name,jdbcType=VARCHAR},'%')
  )

mapper.select:
select count(*)
from anima_res t
where t.code = #{code}

mapper.update:
update anima_res
set status = #{status,jdbcType=CHAR}
where id = #{id,jdbcType=VARCHAR}
