mapper.select:
select b.id, b.code, b.name, b.type, b.depict, b.address, b.longitude, b.latitude, b.mainTag, b.state, b.subareaCode, b.remark, b.status, b.createUserId, b.createUserName, b.createDate, b.lastModifyUserId, b.lastModifyUserName, b.lastModifyDate, b.stationCode, t.id as t_id, t.code as stationCode, t.subareaCode, t.name as stationName, t.depict, t.address, t.latitude,t.longitude, s.name as subareaName, ( SELECT COUNT(*) FROM anima_lnk a WHERE a.lnkCode = b.code) AS animaCount from top_building b LEFT JOIN top_station t on t.code = b.stationCode LEFT JOIN top_subarea s ON t.subareaCode = s.code
and b.stationCode =#{condition.stationCode,jdbcType=VARCHAR}
and t.subareaCode =#{condition.subareaCode,jdbcType=VARCHAR}
ORDER BY ${orderBy}
ORDER BY b.createDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
where 1 = 1

  and b.code =#{condition.code,jdbcType=CHAR}


  and b.status =#{condition.status,jdbcType=CHAR}


  and (b.`name` like concat('%',#{condition.name,jdbcType=VARCHAR},'%')
  )


  and b.type =#{condition.type,jdbcType=CHAR}

mapper.select:
select count(*)
from top_building b
where b.code = #{code}
and b.`name` = #{name}

mapper.update:
update top_building
set status = #{status,jdbcType=CHAR}
where id = #{id,jdbcType=VARCHAR}
