mapper.select:
select




        ORDER BY ${orderBy}


        ORDER BY t.createDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
from counselor t left join studio s on s.id = t.studioId left join teacher_audit ta on ta.counselorId=t.id where t.status <> 0
and t.studioId = #{condition.studioId,jdbcType=VARCHAR}
and t.name like concat('%',#{condition.name,jdbcType=VARCHAR},'%')
and IFNULL(ta.rst,'0') = #{condition.regAuditRst,jdbcType=VARCHAR}
and t.tags like concat('%',#{condition.tags,jdbcType=VARCHAR},'%')
and EXISTS ( select 1 from consult_product cp where cp.counselorId = t.id and cp.type in
#{item}
)
