mapper.select:
select

from course t
where t.id = #{id,jdbcType=VARCHAR}

select




        ORDER BY ${orderBy}


        ORDER BY t.createDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
from course t
where 1=1

    and IFNULL(t.status,'1') = #{condition.status,jdbcType=VARCHAR}

mapper.select:
select count(1)
from course
where name = #{name,jdbcType=VARCHAR}
