mapper.sql:
from teacher_audit where 0=0

  and id =#{condition.id,jdbcType=VARCHAR}


  and teacherId =#{condition.teacherId,jdbcType=VARCHAR}


  and auditor =#{condition.auditor,jdbcType=VARCHAR}


  and statement like
  concat('%',#{condition.statement,jdbcType=VARCHAR},'%')


  and rst = #{condition.rst,jdbcType=VARCHAR}


  and createDate = #{condition.createDate,jdbcType=TIMESTAMP}

mapper.insert:
insert into teacher_audit (id, teacherId, auditor, 
  statement, rst, createDate
  )
values (#{id,jdbcType=VARCHAR}, #{teacherId,jdbcType=VARCHAR}, #{auditor,jdbcType=VARCHAR}, 
  #{statement,jdbcType=VARCHAR}, #{rst,jdbcType=CHAR}, #{createDate,jdbcType=TIMESTAMP}
  )

mapper.select:
select




    ORDER BY ${orderBy}


    ORDER BY createDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
from teacher_audit where
id =#{condition.id,jdbcType=VARCHAR}
