mapper.sql:
from live where deptId=#{condition.deptId}

    and name like
    concat('%',#{condition.name,jdbcType=VARCHAR},'%')


    and category =#{condition.category,jdbcType=VARCHAR}


    and status =#{condition.status,jdbcType=CHAR}

mapper.select:
select




        ORDER BY ${orderBy}


        ORDER BY startTime desc



limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.update:
update live
set
nop = #{nop,jdbcType=BIGINT},
pop = #{pop,jdbcType=BIGINT}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, rid, uid, content, date_format(createTime,'%H:%i:%s') createTime from live_msg where
rid=#{rid}
order by createTime
