mapper.update:
update live_rpt
set
content = #{content,jdbcType=LONGVARCHAR},
mediaType=#{mediaType,jdbcType=VARCHAR},
mediaContent = #{mediaContent,jdbcType=LONGVARCHAR}

where id = #{id,jdbcType=VARCHAR}

update live_rpt
set
status = #{status,jdbcType=CHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.sql:
from live_rpt t
left join live c on t.rid=c.id
left join portal.userinfo a on t.uid=a.openid
left join live_img b on t.id=b.rptId
left join portal.users e on t.uid=e.openId
left join portal.department f on e.department_id=f.department_id
left join live_log q on t.lastAuditLogId=q.id
where c.deptId=#{condition.deptId}

    and t.content like
    concat('%',#{condition.content,jdbcType=VARCHAR},'%')


    and t.rid =#{condition.rid,jdbcType=VARCHAR}


    and t.uid =#{condition.uid,jdbcType=VARCHAR}


    and t.mediaType =#{condition.mediaType,jdbcType=VARCHAR}


    and t.status =#{condition.status,jdbcType=VARCHAR}

mapper.select:
SELECT COUNT(*)
from live_rpt t
left join live c on t.rid=c.id

where c.deptId=#{condition.deptId}

    and t.content like
    concat('%',#{condition.content,jdbcType=VARCHAR},'%')


    and t.rid =#{condition.rid,jdbcType=VARCHAR}


    and t.uid =#{condition.uid,jdbcType=VARCHAR}


    and t.mediaType =#{condition.mediaType,jdbcType=VARCHAR}


    and t.status =#{condition.status,jdbcType=VARCHAR}

select t.id, t.rid, t.uid, t.sort, t.mediaType, t.createTime, t.status, t.likeNum,t.content, t.mediaContent,c.name liveName, a.nickname reporter, '' thumbnail, ifnull(f.logo,a.headimgurl) headimgurl, ifnull(f.short_name,a.nickname) nickname, a.openid, b.id imgId, b.url, q.statement,q.createDate auditDate
ORDER BY t.${orderBy}
ORDER BY t.createTime desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
from
live_rpt where
content=#{content} and rid = #{rid,jdbcType=VARCHAR}
and uid = #{uid,jdbcType=VARCHAR}

mapper.update:
update live_rpt
set
sort = #{sort,jdbcType=CHAR}
where id = #{id,jdbcType=VARCHAR}

update live_rpt
set
status = #{auditStatus},
lastAuditLogId = #{lastAuditLogId}
where id = #{id,jdbcType=VARCHAR}
