mapper.select:
select
t.meetingId, t.topicId,t.remark, t.normId, ifnull(a.productId,t.productId) productId, t.name probDiscri, a.probAnsys, a.actions, a.liable,
a.deptId,a.category,a.status,t.${orderBy},ifnull(b.name,'') userName,a.lastModifyDate,a.id,ifnull(c.department_name,'') deptName,
attach.attach_id,attach.file_name ,attach.file_url



    ORDER BY t.${orderBy}


    ORDER BY t.${orderBy}



limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
from
tpa where
probDiscri=#{probDiscri}
and meetingId=#{meetingId}
and topicId=#{topicId}
and  normId=#{normId}

mapper.sql:
from tpa t left join portal.users b on t.liable=b.user_id left join meeting c on t.meetingId=c.id left join topic d on t.topicId=d.id left join norm e on t.normId=e.id where 0=0
and t.liable=#{condition.liable}
and t.status=#{condition.status}
and b.department_id=#{condition.deptId}
and t.lastModifyDate>=str_to_date(#{condition.startDate},'%Y-%m-%d')
and t.lastModifyDate<=str_to_date(#{condition.endDate},'%Y-%m-%d')

mapper.select:
select m.* from(
select
t.meetingId, t.topicId, t.normId, t.productId, t.probDiscri, t.probAnsys, t.actions, t.liable,
t.status,ifnull(b.name,'') userName,t.lastModifyDate,t.id,t.createDate,e.name meetingName,
d.name topicName,e.name normName
) m


    ORDER BY m.${orderBy}


    ORDER BY m.lastModifyDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
    from tpa t
    left join portal.users b on t.liable=b.user_id
    left join portal.department c on t.deptId=c.department_id
    left join portal.attach attach on t.id=attach.notice_id

    where t.meetingId=#{condition.meetingId}
    and   t.topicId=#{condition.topicId}
    and   t.normId=#{condition.normId}

      and t.productId=#{condition.productId}


      and t.liable=#{condition.liable}


      and t.status=#{condition.status}



			and t.createDate>=str_to_date(#{condition.startDate},'%Y-%m-%d')




				and t.createDate<=str_to_date(#{condition.endDate},'%Y-%m-%d')
