mapper.sql:
where 1 = 1

    and c.status =#{condition.status,jdbcType=VARCHAR}


    and c.classroomId =#{condition.classroomId,jdbcType=VARCHAR}


    and (c.name like concat('%',#{condition.keyword,jdbcType=VARCHAR},'%')
    or cr.name like concat('%',#{condition.keyword,jdbcType=VARCHAR},'%')
    )

mapper.select:
select c.id, c.name, c.startDate, c.endDate, c.introduce,c.photoUrl, c.fileUrl, c.tid1, c.tid2, c.classroomId, c.headmaster, c.remark, c.status, c.createUserId, c.createUserName, c.createDate, c.lastModifyUserId, c.lastModifyUserName, c.lastModifyDate, cr.name as crName, t.name as tName
ORDER BY ${orderBy}
ORDER BY t.createDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
from classes
where
name=#{name}
and id != #{id,jdbcType=VARCHAR}

mapper.update:
update classes
set status = #{status,jdbcType=CHAR}
where id = #{id,jdbcType=VARCHAR}
