mapper.select:
select





        ORDER BY ${orderBy}


        ORDER BY t.createDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select



and t.id not in(
SELECT ts.classroomId
FROM (SELECT p.classroomId
FROM partyschool.classes p
WHERE p.`status`=1
GROUP BY p.classroomId) ts
WHERE 1=1

    and ts.classroomId  <>  #{condition.id,jdbcType=VARCHAR}

)


        ORDER BY ${orderBy}


        ORDER BY t.createDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.update:
update classroom
set status = #{status,jdbcType=CHAR}
where id = #{id,jdbcType=VARCHAR}
