mapper.select:
select





    ORDER BY ${orderBy}


    ORDER BY t.createDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(1)
from course c
where c.status  <>  0
and c.name = #{condition.name,jdbcType=VARCHAR}
and c.teacherId= #{condition.teacherId,jdbcType=VARCHAR}
