mapper.select:
select f.id, f.category, c.name as classesId, f.title, f.url, f.pushDate, f.publisher, f.STATUS
LEFT JOIN classes c on c.id=f.classesId
ORDER BY ${orderBy}
ORDER BY f.pushDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.update:
update files
set status = #{status,jdbcType=CHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select f.id, f.category, f.classesId, f.title, f.url, f.pushDate, f.publisher, f.status
FROM files f
where
f.classesId=(
SELECT s.classId FROM student s WHERE s.id=#{condition.id,jdbcType=VARCHAR}
)


        ORDER BY ${orderBy}


        ORDER BY f.pushDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select f.id, f.category, f.classesId, f.title, f.url, f.pushDate, f.publisher, f.status
FROM files f
where
f.classesId in(
SELECT s.clsId FROM teacher_cls_ship s WHERE s.teacherId=#{condition.teacherId,jdbcType=VARCHAR}
)


        ORDER BY ${orderBy}


        ORDER BY f.pushDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}
