mapper.update:
update mail_list
set name = #{name,jdbcType=VARCHAR},
  pid = #{pid,jdbcType=VARCHAR},
  createDate = #{createDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select ID,PID,name as TEXT,mobile HREF,0 CHILD_COUNT,'closed' STATE from student t where t.classId=#{classId}
UNION ALL
select t.ID,t.PID,t.name as TEXT,null HREF,(select count(1) from student a where a.classId=#{classId} and a.pid=t.id) CHILD_COUNT,'closed' STATE from mail_list t
