mapper.update:
update mail_list
set name = #{name,jdbcType=VARCHAR},
  pid = #{pid,jdbcType=VARCHAR},
  createDate = #{createDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT t.ID, t.PID, t.name AS TEXT, t.mobile HREF, 0 CHILD_COUNT, 'closed' STATE, b.headimgurl ICON FROM student t LEFT JOIN portal.users a on t.id=a.user_id left join portal.userinfo b on a.openId=b.unionid
WHERE t.classId = #{classId}
and t.name like concat(#{name,jdbcType=VARCHAR},'%')
order by t.name
UNION ALL SELECT t.ID, '0' PID, t.name AS TEXT, NULL HREF, ( SELECT count(1) FROM student a WHERE a.classId = #{classId} AND a.pid = t.id ) CHILD_COUNT, 'closed' STATE, null ICON FROM mail_list t where t.pid=#{classId}
