mapper.select:
select count(*)
from portal.users
where mobile = #{tel}
or account = #{tel}

select ui.nickname, ui.headimgurl, u.sex, u.user_level as regType,
s.id as sid, s.pid as sPid, s.name as sName, s.mobile as sMobile, s.idCard as sIdCard, s.political as sPolitical, s.workUnitName as sWUName, s.postName as sPostName, s.classId as sClsId, '' as sClsName,
t.id as tid, t.category as tType, t.name as tName, t.mobile as tMobile, t.idCard as tIdCard, t.photoUrl as tPhotoUrl from portal.users u left join portal.userinfo ui on u.openId = ui.unionid left join partyschool.student s on u.user_id = s.id left join partyschool.teacher t on u.user_id = t.id where u.account = #{acct,jdbcType=VARCHAR}

select
t.user_id, t.account, t.sex, t.openId, t.appOpenId, t.id_card, t.name, t.department_id, s.department_name, t.area_code, s2.name area_name, t.birthday, t.stauts, t.last_login_time, t.mobile, t.email, t.telphone, t.qq, t.fax, t.seat, t.user_level, t.create_time, s3.nickname, s3.headimgurl,s4.nickName, s4.avatarUrl
from portal.users t left join portal.department s on t.department_id=s.department_id left join portal.province s2 on t.area_code=s2.code left join portal.userinfo s3 on t.openId=s3.unionid left join portal.wx_user s4 on t.appOpenId=s4.unionId where t.openId = #{uid,jdbcType=VARCHAR} and t.cur_syid= #{sysId,jdbcType=VARCHAR}

mapper.update:
update portal.users
set openId = #{unionid,jdbcType=CHAR},
appOpenId = #{unionid,jdbcType=CHAR}
where cur_syid = #{sysId,jdbcType=VARCHAR}
and account = #{account,jdbcType=VARCHAR}
