mapper.select:
SELECT 'eva' id, count(1) as VALUE FROM class_schedule a WHERE a.classesId = #{classId}
AND a.courseDate <= now()
AND a.id NOT IN ( SELECT DISTINCT (t.classScheduleId) FROM evaluation_rst_content t WHERE t.userId = #{userId} ) UNION ALL SELECT 'teacher' id, count(1) VALUE FROM teacher t WHERE t. STATUS = '1' UNION ALL SELECT 'student' id, count(1) VALUE FROM student t WHERE t.STATUS = '1' and t.classId=#{classId} UNION ALL SELECT 'file' id, count(1) VALUE FROM files t WHERE t.STATUS = '1'
and t.classesId=#{classId}
and t.classesId in #{item.classId}
