mapper.select:
SELECT 'eva' id, count(1) as VALUE FROM class_schedule a WHERE a.classesId = #{classId}
AND a.courseDate <= NOW()
AND a.id NOT IN ( SELECT DISTINCT (t.classScheduleId) FROM evaluation_rst_content t WHERE t.userId = #{userId} ) UNION ALL SELECT 'teacher' id, count(1) VALUE FROM teacher t WHERE t. STATUS = '1' UNION ALL SELECT 'student' id, count(1) VALUE FROM student t WHERE t.STATUS = '1' and t.classId=#{classId} UNION ALL
SELECT 'test' id, COUNT(1) FROM class_schedule cs WHERE cs.courseDate < NOW() AND cs.classesId = #{classId} AND cs.ifTest ='1' AND cs.id NOT IN ( SELECT distinct classScheduleId FROM partyschool.evaluation_rst er WHERE er.userId= #{userId} ) UNION ALL SELECT 'file' id, count(1) VALUE FROM files t WHERE t.STATUS = '1'
and t.classesId=#{classId}
and t.classesId in #{item.classId}
