mapper.sql:
where 1 = 1

    and t.userId = #{condition.userId,jdbcType=VARCHAR}


    and t.userType = #{condition.userType,jdbcType=VARCHAR}


    and t.attTime = #{condition.attTime,jdbcType=VARCHAR}


    and t.longitude = #{condition.longitude,jdbcType=VARCHAR}


    and t.latitude = #{condition.latitude,jdbcType=VARCHAR}


    and t.attTime like concat(#{condition.dateTimeStr,jdbcType=VARCHAR},'%')


    and s.`name` like concat(#{condition.name,jdbcType=VARCHAR},'%')

mapper.select:
select





        ORDER BY ${orderBy}


        ORDER BY t.createDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}
