mapper.sql:
from appeal_case where appealId=#{condition.appealId}

  and title like
  concat(#{condition.title,jdbcType=VARCHAR},'%')

mapper.select:
select




    ORDER BY ${orderBy}


    ORDER BY createDate


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select
t.id, t.appealId, t.category, t.title, t.mediType, t.mediUrl, t.submitTime, t.submitOpenId, t.latitude,
t.longitude, t.answerTime, t.answerOpenId, t.status, t.createDate from appeal_case t
inner join appeal a on t.appealId=a.id

  and t.submitOpenId =#{submitOpenId,jdbcType=VARCHAR}


  and a.openId =#{answerOpenId,jdbcType=VARCHAR}


  and status =#{status,jdbcType=VARCHAR}

ORDER BY createDate desc

mapper.update:
update appeal_case
set
status='2' ,detailsOfProgress= #{detailsOfProgress,jdbcType=LONGVARCHAR} where id = #{id,jdbcType=VARCHAR}
