mapper.sql:
from appeal_case t
inner join appeal a on t.appealId=a.id
inner join users b on a.createUserId=b.user_id
where b.department_id=#{condition.corpId}

    and t.title like concat(#{condition.title,jdbcType=VARCHAR},'%')


    and t.status = #{condition.status,jdbcType=VARCHAR}

mapper.select:
select a.name as appealName, (select c.name from users c where c.appOpenId = t.submitOpenId ) as realName, (select c.nickName from wx_user c where c.openId = t.submitOpenId ) as nickName, t.id, t.appealId, t.category, t.title, t.mediType, t.mediUrl, t.companyName, t.submitName, t.tel, t.submitTime, t.submitOpenId, t.latitude, t.longitude, t.answerDept, t.answerTime, t.answerOpenId, t.remak, t.status, t.createDate, t.content, t.answerContent, t.detailsOfProgress, t.operator, ( CASE WHEN t.status = '1' THEN '待受理' WHEN t.status = '2' THEN '已受理' WHEN t.status = '3' THEN '办结' ELSE '其他' END ) statusName
ORDER BY t.${orderBy}
ORDER BY t.createDate
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
    from appeal_case
    where title = #{title}
    and appealId = #{appealId}
    and submitOpenId = #{submitOpenId}

select t.id, t.appealId, t.category, t.title,t.content,t.answerContent, t.mediType, t.mediUrl,DATE_FORMAT(t.submitTime,'%Y-%m-%d %T') submitTime, t.submitOpenId, t.latitude, t.longitude, t.answerTime, t.answerOpenId, t.status, t.createDate,t.companyName, t.submitName, t.tel, t.detailsOfProgress, t.operator, ( CASE WHEN t.status = '1' THEN '待受理' WHEN t.status = '2' THEN '已受理' WHEN t.status = '3' THEN '办结' ELSE '其他' END ) statusName from appeal_case t inner join appeal a on t.appealId=a.id
and t.submitOpenId =#{submitOpenId,jdbcType=VARCHAR}
and a.openId =#{answerOpenId,jdbcType=VARCHAR}
and status =#{status,jdbcType=VARCHAR}
ORDER BY createDate desc

mapper.update:
update appeal_case
set
    status='2',
    answerDept=#{answerDept,jdbcType=VARCHAR},
    operator = #{operator,jdbcType=VARCHAR}

    where id = #{id,jdbcType=VARCHAR}

update appeal_case
set
    status='2' ,
    detailsOfProgress= #{detailsOfProgress,jdbcType=LONGVARCHAR}
    where id = #{id,jdbcType=VARCHAR}
