mapper.sql:
from article where 0=0

  and name like
  concat(#{condition.name,jdbcType=VARCHAR},'%')


  and tplPageId =#{condition.tplPageId,jdbcType=VARCHAR}


  and articleCategory =#{condition.articleCategory,jdbcType=VARCHAR}

mapper.select:
select




    ORDER BY ${orderBy}


    ORDER BY createDate



limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select id, tplPageId, articleCategory, title, cover, remark, hrefUrl, tags, mediType, mediUrl, bgMusicUrl, likeNum, hitNum, sort from article where tplPageId =#{pageId,jdbcType=VARCHAR}
and articleCategory =#{category,jdbcType=VARCHAR}
ORDER BY sort,createDate

select id,title, cover, remark, hrefUrl, tags, mediType, mediUrl, bgMusicUrl, likeNum, hitNum, sort from article where tplPageId =#{pageId,jdbcType=VARCHAR}
and articleCategory =#{category,jdbcType=VARCHAR}
ORDER BY sort,createDate
