mapper.select:
select unionid, openid, appid, nickname, sex, city, province, country, headimgurl, access_token, refresh_token, scope, expires_in,role from userinfo where 0=0
and nickname like concat(#{condition.nickname,jdbcType=VARCHAR},'%')
and role =#{condition.role,jdbcType=VARCHAR}
and openid =#{condition.openid,jdbcType=VARCHAR}
ORDER BY unionid
