mapper.sql:
from wx_user where 0=0
and name like concat(#{condition.name,jdbcType=VARCHAR},'%')
and mobile =#{condition.mobile,jdbcType=VARCHAR}
and nickName like concat(#{condition.nickName,jdbcType=VARCHAR},'%')
and role =#{condition.role,jdbcType=VARCHAR}

