mapper.select:
select




    ORDER BY ${orderBy}


    ORDER BY t.createDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
from behavior t left join portal.wx_user wx on t.userId = wx.unionId left join portal.users u on u.user_id = wx.unionId left join behavior_annex nex on t.id = nex.behaviorId where 1=1
and t.title like concat('%',#{condition.title,jdbcType=VARCHAR},'%')
and t.status = #{condition.status,jdbcType=CHAR}
and t.id = #{condition.id,jdbcType=VARCHAR}

mapper.update:
update behavior
set status = #{status,jdbcType=CHAR},
    lastModifyUserId = #{lastModifyUserId,jdbcType=VARCHAR},
    lastModifyUserName = #{lastModifyUserName,jdbcType=VARCHAR},
    lastModifyDate = #{lastModifyDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}
