  rewardPoints, remark, status, 
  createUserId, createUserName, createDate, 
  lastModifyUserId, lastModifyUserName, lastModifyDate
  )
values (#{id,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{content,jdbcType=CHAR}, 
  #{rewardPoints,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, #{status,jdbcType=CHAR}, 
  #{createUserId,jdbcType=VARCHAR}, #{createUserName,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, 
  #{lastModifyUserId,jdbcType=VARCHAR}, #{lastModifyUserName,jdbcType=VARCHAR}, #{lastModifyDate,jdbcType=TIMESTAMP}
  )

mapper.select:
select




    ORDER BY ${orderBy}


    ORDER BY t.createDate desc


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
from subject t where 1=1
and t.title like concat('%',#{condition.title,jdbcType=VARCHAR},'%')
and t.status = #{condition.status,jdbcType=CHAR}
