mapper.select:
select wx.avatarUrl, wx.nickName, u.user_level as regType,
t.id, t.subjectId ,t.title, t.solution, t.userId, t.remark, t.status, t.createUserId, t.createUserName, t.createDate, t.lastModifyUserId, t.lastModifyUserName, t.lastModifyDate, t.remark, t.category, e.id as annexId, e.ideaId, e.fileType, e.fileName, e.fileSize, e.fileUrl, e.remark as eremark
ORDER BY ${orderBy}
ORDER BY t.createDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
from subject_idea t
LEFT JOIN subject_idea_annex e on t.id = e.ideaId
left join portal.wx_user wx on t.userId = wx.unionId
left join  portal.users u on u.user_id = wx.unionId
where 1=1

    and t.solution like concat('%',#{condition.solution,jdbcType=VARCHAR},'%')


    and t.subjectId = #{condition.subjectId,jdbcType=VARCHAR}


    and t.userId = #{condition.userId,jdbcType=VARCHAR}


    and t.status = #{condition.status,jdbcType=VARCHAR}


    and t.category = #{condition.category,jdbcType=VARCHAR}

mapper.update:
update subject_idea
set status = #{status,jdbcType=CHAR},
    lastModifyUserId = #{lastModifyUserId,jdbcType=VARCHAR},
    lastModifyUserName = #{lastModifyUserName,jdbcType=VARCHAR},
    lastModifyDate = #{lastModifyDate,jdbcType=TIMESTAMP}
  where id = #{id,jdbcType=VARCHAR}
