mapper.select:
select count(*)
from portal.users
where mobile = #{tel}
or account = #{tel}

mapper.update:
update portal.users
set mobile = #{mobile,jdbcType=CHAR}
where user_id = #{userId,jdbcType=VARCHAR}

update portal.users
set stauts = #{status,jdbcType=CHAR}
where user_id = #{userId,jdbcType=VARCHAR}

mapper.select:
select w.nickName, w.avatarUrl, d.department_name, t.user_id as copNo, t.`name`, t.mobile from portal.users t left join portal.department d on t.department_id = d.department_id left join portal.wx_user w on t.appOpenId = w.unionId where 1=1 and t.cur_syid = #{sysId,jdbcType=VARCHAR}
and t.user_id = #{userId,jdbcType=VARCHAR}
and t.appOpenId = #{uid,jdbcType=VARCHAR}
and t.account = #{account,jdbcType=VARCHAR}
