mapper.select:
select * from (
    select
    t.id, t.roadId, t.areaCode, t.roadMan, t.name, t.startName, t.endName,
    t.startNo, t.endNo, t.status,a.name roadManName,b.name areaName,t.createDate

    )m


        ORDER BY m.${orderBy}


        ORDER BY m.createDate



    limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select t.id, t.roadId, t.areaCode, t.roadMan, t.name, t.startName, t.endName, t.startNo, t.endNo, t.status,a.name roadManName,b.name areaName from road_section t left join road_man a on t.roadMan=a.id left join portal.province b on t.areaCode=b.code where 0=0
and t.roadId=#{p.id}
and t.roadMan=#{p.roadMan}
order by t.startNo

