mapper.select:
select p.`name` as partyName, d.department_name as partyDeptName, tc.plateNo, tc.axleCount,tc.totalMass, tc.overMass, tc.overRate, tc.speed, t.id, t.caseNo, t.caseDate, t.trafficId, t.vehicleId, t.driver, t.party, t.partyType, t.chp1, t.lecn, t.chp2, t.recorder, t.recordTime, t.fines, t.auditDept, t.remark, t.status, t.createUserId, t.createUserName, t.createDate, t.lastModifyUserId, t.lastModifyUserName, t.lastModifyDate
ORDER BY ${orderBy}
ORDER BY t.createDate
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
from woc.`case`
where id = #{id,jdbcType=VARCHAR}
