mapper.select:
select




        ORDER BY ${orderBy}


        ORDER BY t.createDate


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
from woc.cases t
left join woc.traffic tc on t.trafficId = tc.id
left join woc.person p on t.driver = p.id
left join woc.vehicle v on t.vehicleId = v.id
where 1 = 1

    and t.caseNo like concat(#{condition.caseNo,jdbcType=VARCHAR},'%')


    and tc.plateNo = #{condition.plateNo,jdbcType=VARCHAR}


    and t.caseDate >= #{condition.startDate,jdbcType=VARCHAR}


    and t.caseDate <= #{condition.endDate,jdbcType=VARCHAR}

mapper.select:
select count(*)
from woc.cases
where id = #{id,jdbcType=VARCHAR}

SELECT ( SELECT COUNT(1) FROM cases WHERE STATUS='1'
AND siteId = #{params.siteId,jdbcType=VARCHAR}
AND UNIX_TIMESTAMP(caseDate) >UNIX_TIMESTAMP(#{params.startDt,jdbcType=TIMESTAMP})
AND UNIX_TIMESTAMP(caseDate) <=UNIX_TIMESTAMP(#{params.endDt,jdbcType=TIMESTAMP})
) perfectionCases,
( SELECT COUNT(1) FROM cases WHERE STATUS='2'
AND siteId = #{params.siteId,jdbcType=VARCHAR}
AND UNIX_TIMESTAMP(caseDate) >UNIX_TIMESTAMP(#{params.startDt,jdbcType=TIMESTAMP})
AND UNIX_TIMESTAMP(caseDate) <=UNIX_TIMESTAMP(#{params.endDt,jdbcType=TIMESTAMP})
) unauditedCases,
( SELECT COUNT(1) FROM cases WHERE STATUS='4'
AND siteId = #{params.siteId,jdbcType=VARCHAR}
AND UNIX_TIMESTAMP(caseDate) >UNIX_TIMESTAMP(#{params.startDt,jdbcType=TIMESTAMP})
AND UNIX_TIMESTAMP(caseDate) <=UNIX_TIMESTAMP(#{params.endDt,jdbcType=TIMESTAMP})
) verifyCases FROM cases LIMIT 0, 1
