mapper.select:
select d.deviceName, a.id, a.monitorSiteId, a.deviceId, a.status, a.createUserId, a.createUserName, a.createDate, a.lastModifyUserId, a.lastModifyUserName, a.lastModifyDate
ORDER BY ${orderBy}
ORDER BY createDate
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
from monitor_site_detail
where deviceId= #{deviceId,jdbcType=VARCHAR}
  and monitorSiteId != #{monitorSiteId,jdbcType=VARCHAR}
