mapper.select:
select p.`name` as areaName, d.department_name, r.id,r.roadName,r.roadCode,r.areaCode,r.roadLength,r.constructDate,r.adminDepId,r.roadStatus, r.remark,r.`status`,r.createUserId,r.createUserName,r.lastModifyUserId,r.lastModifyUserName,r.lastModifyDate
ORDER BY ${orderBy}
ORDER BY createDate
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

select count(*)
from road
where roadName = #{roadName,jdbcType=VARCHAR}
